// File: assets/js/app.js
function initSidebarToggler() {
  var setSidebarType = function () {
    var width = window.innerWidth > 0 ? window.innerWidth : this.screen.width;
    if (width < 1199) {
      $("#main-wrapper").attr("data-sidebartype", "mini-sidebar").addClass("mini-sidebar");
    } else {
      $("#main-wrapper").attr("data-sidebartype", "full").removeClass("mini-sidebar");
    }
  };

  $(window).off("resize").on("resize", setSidebarType).ready(setSidebarType);

  $(".sidebartoggler").off("click").on("click", function () {
    $("#main-wrapper").toggleClass("mini-sidebar");
    if ($("#main-wrapper").hasClass("mini-sidebar")) {
      $(".sidebartoggler").prop("checked", true);
      $("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
    } else {
      $(".sidebartoggler").prop("checked", false);
      $("#main-wrapper").attr("data-sidebartype", "full");
    }
    $("#main-wrapper").toggleClass("show-sidebar");
  });
}

// Panggil saat dokumen siap
$(document).ready(initSidebarToggler);

// Ekspor fungsi untuk dipanggil ulang
window.initSidebarToggler = initSidebarToggler;